    const namespace = this.options?.namespace ?? "argocd"
    const version = this.options?.version ?? "v3.0.6"

    // Create namespace for Argo CD
    const nsManifest = cluster.addManifest("argocd-namespace", {
      apiVersion: "v1",
      kind: "Namespace",
      metadata: {
        name: namespace,
      },
    })

