                logger.info("Recursos eliminados con éxito")
            except subprocess.CalledProcessError as e:
                logger.warning(f"Error al eliminar recursos: {str(e)}")

            # Eliminar CRDs de ArgoCD
            try:
                logger.info("Eliminando CRDs de ArgoCD")
                subprocess.check_call(['kubectl', 'delete', 'crd', '-l', 'app.kubernetes.io/part-of=argocd'])
                logger.info("CRDs eliminados con éxito")
            except subprocess.CalledProcessError as e:
                logger.warning(f"Error al eliminar CRDs: {str(e)}")

            # Eliminar el namespace si existe
            try:
                logger.info(f"Eliminando namespace {namespace}")
                subprocess.check_call(['kubectl', 'delete', 'namespace', namespace, '--ignore-not-found'])
                logger.info("Namespace eliminado con éxito")
            except subprocess.CalledProcessError as e:
                logger.warning(f"Error al eliminar namespace: {str(e)}")

            logger.info("Limpieza de ArgoCDCore completada")
            response_data['Message'] = "ArgoCDCore desinstalado correctamente"
        else:
            # Para Create y Update, simplemente devolver éxito
            logger.info(f"Operación {request_type} - no se requiere acción")
            response_data['Message'] = f"Operación {request_type} procesada"

        cfnresponse.send(event, context, cfnresponse.SUCCESS, response_data)
    except Exception as e:
        logger.error(f"Error: {str(e)}")
        cfnresponse.send(event, context, cfnresponse.FAILED, {"Error": str(e)})
`),
      timeout: cdk.Duration.minutes(15),
