    fn.addToRolePolicy(
      new cdk.aws_iam.PolicyStatement({
        actions: ["eks:DescribeCluster", "eks:ListClusters"],
        resources: ["*"],
      })
    )

    // Create a custom provider for the resource
    return new cdk.custom_resources.Provider(
      cluster,
      "argo-rollouts-cleanup-provider",
      {
        onEventHandler: fn,
      }
    )
  }
}"}

