import * as eks from "aws-cdk-lib/aws-eks"
import * as cdk from "aws-cdk-lib"

export interface ArgoCDAddOnProps {
  namespace?: string;
  version?: string;
  values?: { [key: string]: any };
  createNamespace?: boolean;
  cleanupEnabled?: boolean;
}

export class ArgoCDAddOn implements ClusterAddOn {
  private resources: Construct[] = [];
  private readonly cleanupEnabled: boolean;
  private readonly namespace: string;

  constructor(private props: ArgoCDAddOnProps = {}) {
    this.cleanupEnabled = props.cleanupEnabled ?? true;
    this.namespace = props.namespace ?? "argocd";
  }

  deploy(clusterInfo: ClusterInfo): Promise<Construct> {
    const cluster = clusterInfo.cluster
    const namespace = this.namespace;
    const version = this.props.version ?? "5.51.4" // Versión actual del chart de Helm
    const createNamespace = this.props.createNamespace ?? true

    let chart: eks.HelmChart;

    if (createNamespace) {
      // Crear namespace para Argo CD
      const nsManifest = cluster.addManifest("argocd-namespace", {
        apiVersion: "v1",
        kind: "Namespace",
        metadata: {
          name: namespace
        }
      });

      // Instalar Argo CD usando Helm
      chart = cluster.addHelmChart("argocd", {
        repository: "https://argoproj.github.io/argo-helm",
        chart: "argo-cd",
        release: "argocd",
        namespace: namespace,
        version: version,
        values: this.props.values
      });

      chart.node.addDependency(nsManifest);
      this.resources.push(nsManifest);
    } else {
      // Instalar Argo CD usando Helm sin crear namespace
      chart = cluster.addHelmChart("argocd", {
        repository: "https://argoproj.github.io/argo-helm",
        chart: "argo-cd",
        release: "argocd",
        namespace: namespace,
        version: version,
        values: this.props.values
      });
    }

    this.resources.push(chart);

    // Agregar mecanismo de cleanup si está habilitado
    if (this.cleanupEnabled) {
      this.setupCleanupMechanism(cluster, namespace);
    }

    return Promise.resolve(chart);
  }

  private setupCleanupMechanism(cluster: eks.Cluster, namespace: string) {
    // Crear un recurso personalizado que se ejecutará durante la eliminación
    const cleanupProvider = new cdk.CustomResource(cluster, "argocd-cleanup-provider", {
      serviceToken: this.createCleanupLambda(cluster, namespace).functionArn,
      properties: {
        // Incluir un timestamp para asegurar que el recurso se actualice cada vez
        timestamp: new Date().getTime(),
        namespace: namespace,
        releaseName: "argocd",
      },
      resourceType: "Custom::ArgoCDCleanup",
    });

    // Asegurar que el cleanup se ejecute después de que se hayan creado los recursos
    for (const resource of this.resources) {
      cleanupProvider.node.addDependency(resource);
    }
  }

  private createCleanupLambda(cluster: eks.Cluster, namespace: string) {
    // Crear una función Lambda que ejecutará el comando helm uninstall
    const fn = new cdk.aws_lambda.Function(cluster, "argocd-cleanup-lambda", {
      runtime: cdk.aws_lambda.Runtime.PYTHON_3_9,
      handler: "index.handler",
      code: cdk.aws_lambda.Code.fromInline(`
import boto3
import cfnresponse
import subprocess
import os

def handler(event, context):
    try:
        if event['RequestType'] == 'Delete':
            # Configurar kubectl para acceder al clúster
            cluster_name = os.environ['CLUSTER_NAME']
            region = os.environ['REGION']

            # Obtener credenciales del clúster
            eks = boto3.client('eks', region_name=region)
            cluster_info = eks.describe_cluster(name=cluster_name)

            # Configurar kubeconfig
            subprocess.check_call(['aws', 'eks', 'update-kubeconfig', '--name', cluster_name, '--region', region])

            # Desinstalar Argo CD usando Helm
            namespace = event['ResourceProperties']['namespace']
            release_name = event['ResourceProperties']['releaseName']

            subprocess.check_call(['helm', 'uninstall', release_name, '-n', namespace])

            # Eliminar el namespace si existe
            subprocess.check_call(['kubectl', 'delete', 'namespace', namespace, '--ignore-not-found'])

            print(f"Argo CD desinstalado correctamente del namespace {namespace}")

        cfnresponse.send(event, context, cfnresponse.SUCCESS, {})
    except Exception as e:
        print(f"Error: {str(e)}")
        cfnresponse.send(event, context, cfnresponse.FAILED, {"Error": str(e)})
`),
      timeout: cdk.Duration.minutes(15),
      environment: {
        CLUSTER_NAME: cluster.clusterName,
        REGION: cdk.Stack.of(cluster).region,
      },
    });

    // Agregar permisos necesarios para la función Lambda
    fn.addToRolePolicy(new cdk.aws_iam.PolicyStatement({
      actions: [
        "eks:DescribeCluster",
        "eks:ListClusters",
      ],
      resources: ["*"],
    }));

    // Crear un proveedor personalizado para el recurso
    return new cdk.custom_resources.Provider(cluster, "argocd-cleanup-provider-lambda", {
      onEventHandler: fn,
    });
  }
}"}]}

