      manifest: [
        {
          apiVersion: "batch/v1",
          kind: "Job",
          metadata: {
            name: "argocd-install-job",
            namespace: namespace,
          },
          spec: {
            template: {
              spec: {
                serviceAccountName: "argocd-installer",
                containers: [
                  {
                    name: "kubectl",
                    image: "bitnami/kubectl:latest",
                    command: ["/bin/sh", "-c"],
                    args: [`kubectl apply -f ${manifestUrl} -n ${namespace}`],
                  },
                ],
                restartPolicy: "Never",
              },
            },
            backoffLimit: 0,
          },
        },
      ],
    })

