import * as eks from "aws-cdk-lib/aws-eks"
import * as cdk from "aws-cdk-lib"

export interface ArgoCDCoreAddOnProps {
  namespace?: string;
  version?: string;
  cleanupEnabled?: boolean;
}

export class ArgoCDCoreAddOn implements ClusterAddOn {
  private readonly cleanupEnabled: boolean;

  constructor(private props: ArgoCDCoreAddOnProps = {}) {
    this.cleanupEnabled = props.cleanupEnabled ?? true;
  }

  deploy(clusterInfo: ClusterInfo): Promise<Construct> {
    const cluster = clusterInfo.cluster
    const namespace = this.props.namespace ?? "argocd"
    const version = this.props.version ?? "v3.0.6"

    // Create namespace for Argo CD
    const nsManifest = cluster.addManifest("argocd-namespace", {
      apiVersion: "v1",
      kind: "Namespace",
      metadata: {
        name: namespace,
      },
    })

    const manifestUrl = `https://raw.githubusercontent.com/argoproj/argo-cd/refs/tags/${version}/manifests/install.yaml`

    // Crear service account para el job de instalación
    const installerSA = cluster.addManifest("argocd-installer-sa", {
      apiVersion: "v1",
      kind: "ServiceAccount",
      metadata: {
        name: "argocd-installer",
        namespace: namespace,
      },
    })

    // Crear role binding para dar permisos al service account
    const installerRoleBinding = cluster.addManifest("argocd-installer-rb", {
      apiVersion: "rbac.authorization.k8s.io/v1",
      kind: "ClusterRoleBinding",
      metadata: {
        name: "argocd-installer-rb",
      },
      subjects: [
        {
          kind: "ServiceAccount",
          name: "argocd-installer",
          namespace: namespace,
        },
      ],
      roleRef: {
        kind: "ClusterRole",
        name: "cluster-admin",
        apiGroup: "rbac.authorization.k8s.io",
      },
    })

    // Configurar para que se aplique el manifiesto completo durante el despliegue
    const applyArgoCD = new eks.KubernetesManifest(cluster, "argocd-apply", {
      cluster: cluster,
      manifest: [
        {
          apiVersion: "batch/v1",
          kind: "Job",
          metadata: {
            name: "argocd-install-job",
            namespace: namespace,
          },
          spec: {
            template: {
              spec: {
                serviceAccountName: "argocd-installer",
                containers: [
                  {
                    name: "kubectl",
                    image: "bitnami/kubectl:latest",
                    command: ["/bin/sh", "-c"],
                    args: [`kubectl apply -f ${manifestUrl} -n ${namespace}`],
                  },
                ],
                restartPolicy: "Never",
              },
            },
            backoffLimit: 0,
          },
        },
      ],
    })

    // Establecer dependencias correctas
    installerSA.node.addDependency(nsManifest)
    installerRoleBinding.node.addDependency(installerSA)
    applyArgoCD.node.addDependency(installerRoleBinding)

    let mainResource = applyArgoCD;

    // Agregar mecanismo de cleanup si está habilitado
    if (this.cleanupEnabled) {
      mainResource = this.setupCleanupMechanism(cluster, namespace, applyArgoCD);
    }

    return Promise.resolve(mainResource)
  }

  private setupCleanupMechanism(
    cluster: eks.ICluster,
    namespace: string,
    resource: Construct
  ): Construct {
    // Crear un recurso personalizado que se ejecutará durante la eliminación
    const cleanupCR = new cdk.CustomResource(cluster, "argocd-core-cleanup-cr", {
      serviceToken: this.createCleanupProvider(cluster).serviceToken,
      properties: {
        // Incluir un timestamp para asegurar que el recurso se actualice cada vez
        timestamp: Date.now().toString(),
        namespace: namespace,
        clusterName: cluster.clusterName,
        region: cdk.Stack.of(cluster).region,
      },
      resourceType: "Custom::ArgoCDCoreCleanup",
    })

    // Asegurar que el cleanup se ejecute después de que se haya creado el recurso principal
    cleanupCR.node.addDependency(resource)

    return cleanupCR
  }

  private createCleanupProvider(
    cluster: eks.ICluster
  ): cdk.custom_resources.Provider {
    // Crear una función Lambda que ejecutará la limpieza
    const fn = new cdk.aws_lambda.Function(cluster, "argocd-core-cleanup-lambda", {
      runtime: cdk.aws_lambda.Runtime.PYTHON_3_9,
      handler: "index.handler",
      code: cdk.aws_lambda.Code.fromInline(`
import boto3
import cfnresponse
import subprocess
import os
import logging

logger = logging.getLogger()
logger.setLevel(logging.INFO)

def handler(event, context):
    logger.info(f"Received event: {event}")
    response_data = {}

    try:
        request_type = event['RequestType']
        properties = event['ResourceProperties']

        # Solo ejecutar la limpieza cuando se elimina el recurso
        if request_type == 'Delete':
            cluster_name = properties['clusterName']
            region = properties['region']
            namespace = properties['namespace']

            logger.info(f"Iniciando limpieza de ArgoCDCore en namespace {namespace}")

            # Configurar kubectl para acceder al clúster
            subprocess.check_call(['aws', 'eks', 'update-kubeconfig', 
                                  '--name', cluster_name, 
                                  '--region', region])

            # Eliminar todos los recursos de ArgoCD
            try:
                logger.info(f"Eliminando recursos de ArgoCD en namespace {namespace}")
                subprocess.check_call(['kubectl', 'delete', 'all', '--all', '-n', namespace])
                logger.info("Recursos eliminados con éxito")
            except subprocess.CalledProcessError as e:
                logger.warning(f"Error al eliminar recursos: {str(e)}")

            # Eliminar CRDs de ArgoCD
            try:
                logger.info("Eliminando CRDs de ArgoCD")
                subprocess.check_call(['kubectl', 'delete', 'crd', '-l', 'app.kubernetes.io/part-of=argocd'])
                logger.info("CRDs eliminados con éxito")
            except subprocess.CalledProcessError as e:
                logger.warning(f"Error al eliminar CRDs: {str(e)}")

            # Eliminar el namespace si existe
            try:
                logger.info(f"Eliminando namespace {namespace}")
                subprocess.check_call(['kubectl', 'delete', 'namespace', namespace, '--ignore-not-found'])
                logger.info("Namespace eliminado con éxito")
            except subprocess.CalledProcessError as e:
                logger.warning(f"Error al eliminar namespace: {str(e)}")

            logger.info("Limpieza de ArgoCDCore completada")
            response_data['Message'] = "ArgoCDCore desinstalado correctamente"
        else:
            # Para Create y Update, simplemente devolver éxito
            logger.info(f"Operación {request_type} - no se requiere acción")
            response_data['Message'] = f"Operación {request_type} procesada"

        cfnresponse.send(event, context, cfnresponse.SUCCESS, response_data)
    except Exception as e:
        logger.error(f"Error: {str(e)}")
        cfnresponse.send(event, context, cfnresponse.FAILED, {"Error": str(e)})
`),
      timeout: cdk.Duration.minutes(15),
    })

    // Agregar permisos necesarios para la función Lambda
    fn.addToRolePolicy(
      new cdk.aws_iam.PolicyStatement({
        actions: ["eks:DescribeCluster", "eks:ListClusters"],
        resources: ["*"],
      })
    )

    // Crear un proveedor personalizado para el recurso
    return new cdk.custom_resources.Provider(
      cluster,
      "argocd-core-cleanup-provider",
      {
        onEventHandler: fn,
      }
    )
  }
}
"}]}

