import { EksBlueprint } from "@aws-quickstart/eks-blueprints"
import * as blueprints from "@aws-quickstart/eks-blueprints"
import { KubernetesVersion } from "aws-cdk-lib/aws-eks"
import { Construct } from "constructs"
import * as eks from "aws-cdk-lib/aws-eks"
import * as ec2 from "aws-cdk-lib/aws-ec2"

export default class KarpernterConstruct {
  constructor(scope: Construct, id: string, version: KubernetesVersion) {
    const account = process.env.CDK_DEFAULT_ACCOUNT!
    const region = process.env.CDK_DEFAULT_REGION!
    const stackID = `${id}`

    const nodeClassSpec: blueprints.Ec2NodeClassV1Spec = {
      amiFamily: "Bottlerocket",
      amiSelectorTerms: [{ alias: "bottlerocket@latest" }],
      subnetSelectorTerms: [
        { tags: { Name: `${stackID}/${stackID}-vpc/PrivateSubnet*` } },
      ],
      securityGroupSelectorTerms: [
        { tags: { "aws:eks:cluster-name": `${stackID}` } },
      ],
    }

    const nodePoolSpec: blueprints.NodePoolV1Spec = {
      labels: { type: "aws-community-day" },
      annotations: {
        "eks-blueprints/owner": "parraletz",
      },
      requirements: [
        {
          key: "node.kubernetes.io/instance-type",
          operator: "In",
          values: ["m5.large", "t3.large"],
        },
        {
          key: "topology.kubernetes.io/zone",
          operator: "In",
          values: ["us-east-1a", "us-east-1b", "us-east-1c"],
        },
        {
          key: "kubernetes.io/arch",
          operator: "In",
          values: ["amd64", "arm64"],
        },
        { key: "karpenter.sh/capacity-type", operator: "In", values: ["spot"] },
      ],
      expireAfter: "1m",
      disruption: { consolidationPolicy: "WhenEmpty", consolidateAfter: "30s" },
    }

    const clusterProvider = new blueprints.GenericClusterProvider({
      version,
      clusterName: stackID,
      managedNodeGroups: [
        {
          id: "managed-nodes",
          instanceTypes: ec2.InstanceType.of(
            ec2.InstanceClass.M7I,
            ec2.InstanceSize.MEDIUM
          ),
        },
      ],
      fargateProfiles: {
        "aws-community-day": {
          fargateProfileName: "aws-community-day",
          selectors: [{ namespace: "karpenter" }],
        },
      },
    })

    const addons: Array<blueprints.ClusterAddOn> = [
      new blueprints.addons.AwsLoadBalancerControllerAddOn({
        version: "1.13.2",
      }),
      new blueprints.addons.VpcCniAddOn(),
      new blueprints.addons.CoreDnsAddOn(),
      new blueprints.addons.KubeProxyAddOn(),
      new blueprints.addons.MetricsServerAddOn({
        name: "metrics-server",
      }),
      new blueprints.addons.KarpenterV1AddOn({
        nodePoolSpec,
        ec2NodeClassSpec: nodeClassSpec,
        interruptionHandling: true,
        namespace: "karpenter",
      }),
      new blueprints.addons.IstioBaseAddOn({
        version: "1.26.1",
      }),
      new blueprints.addons.IstioControlPlaneAddOn({
        version: "1.26.1",
      }),
      new blueprints.addons.IstioIngressGatewayAddon({
        version: "1.26.1",
        values: {
          service: {
            annotations: {
              "service.beta.kubernetes.io/aws-load-balancer-type": "nlb",
            },
          },
        },
      }),
      new blueprints.addons.NginxAddOn({
        version: "2.1.0",
        name: "ingress-nginx",
      }),
    ]

    EksBlueprint.builder()
      .account(account)
      .clusterProvider(clusterProvider)
      .region(region)
      .addOns(...addons)
      .build(scope, stackID)
  }
}

