            subprocess.check_call(['aws', 'eks', 'update-kubeconfig', 
                                  '--name', cluster_name, 
                                  '--region', region])

            # Desinstalar Argo CD usando Helm
            try:
                logger.info(f"Ejecutando helm uninstall {release_name} -n {namespace}")
                subprocess.check_call(['helm', 'uninstall', release_name, '-n', namespace])
                logger.info("Helm uninstall completado con xito")
            except subprocess.CalledProcessError as e:
                logger.warning(f"Error al desinstalar Helm chart: {str(e)}")
                # Continuar con la eliminacin del namespace incluso si falla la desinstalacin

            # Eliminar el namespace si existe
            try:
                logger.info(f"Eliminando namespace {namespace}")
                subprocess.check_call(['kubectl', 'delete', 'namespace', namespace, '--ignore-not-found'])
                logger.info("Namespace eliminado con xito")
            except subprocess.CalledProcessError as e:
                logger.warning(f"Error al eliminar namespace: {str(e)}")

            logger.info("Limpieza de ArgoCD completada")
            response_data['Message'] = "ArgoCD desinstalado correctamente"
        else:
            # Para Create y Update, simplemente devolver xito
            logger.info(f"Operacin {request_type} - no se requiere accin")
            response_data['Message'] = f"Operacin {request_type} procesada"

        cfnresponse.send(event, context, cfnresponse.SUCCESS, response_data)
    except Exception as e:
        logger.error(f"Error: {str(e)}")
        cfnresponse.send(event, context, cfnresponse.FAILED, {"Error": str(e)})
`),
      timeout: cdk.Duration.minutes(15),
    });

    // Agregar permisos necesarios para la funcin Lambda
    fn.addToRolePolicy(new cdk.aws_iam.PolicyStatement({
      actions: [
        "eks:DescribeCluster",
        "eks:ListClusters",
      ],
      resources: ["*"],
    }));

    // Crear un proveedor personalizado para el recurso
    return new cdk.custom_resources.Provider(cluster, "argocd-cleanup-provider", {
      onEventHandler: fn,
    });
  }
}
