","newStr":"import { ClusterAddOn, ClusterInfo } from "@aws-quickstart/eks-blueprints"
import { Construct } from "constructs"
import * as eks from "aws-cdk-lib/aws-eks"

export class ArgoCDCoreAddOn implements ClusterAddOn {
  constructor(private options?: {
    namespace?: string;
    version?: string;
  }) {}

  deploy(clusterInfo: ClusterInfo): Promise<Construct> {
    const cluster = clusterInfo.cluster
    const namespace = this.options?.namespace ?? "argocd"
    const version = this.options?.version ?? "v3.0.6"

    // Create namespace for Argo CD
    const nsManifest = cluster.addManifest("argocd-namespace", {
      apiVersion: "v1",
      kind: "Namespace",
      metadata: {
        name: namespace
      }
    });

    // Aplicar el manifiesto de instalacin de Argo CD desde el repositorio oficial
    const manifestUrl = `https://raw.githubusercontent.com/argoproj/argo-cd/refs/tags/${version}/manifests/install.yaml`;

    // Crear service account para el job de instalacin
    const installerSA = cluster.addManifest("argocd-installer-sa", {
      apiVersion: "v1",
      kind: "ServiceAccount",
      metadata: {
        name: "argocd-installer",
        namespace: namespace
      }
    });

    // Crear role binding para dar permisos al service account
    const installerRoleBinding = cluster.addManifest("argocd-installer-rb", {
      apiVersion: "rbac.authorization.k8s.io/v1",
      kind: "ClusterRoleBinding",
      metadata: {
        name: "argocd-installer-rb"
      },
      subjects: [{
        kind: "ServiceAccount",
        name: "argocd-installer",
        namespace: namespace
      }],
      roleRef: {
        kind: "ClusterRole",
        name: "cluster-admin",
        apiGroup: "rbac.authorization.k8s.io"
      }
    });

    // Configurar para que se aplique el manifiesto completo durante el despliegue
    const applyArgoCD = new eks.KubernetesManifest(cluster, "argocd-apply", {
      cluster: cluster,
      manifest: [{
        apiVersion: "batch/v1",
        kind: "Job",
        metadata: {
          name: "argocd-install-job",
          namespace: namespace
        },
        spec: {
          template: {
            spec: {
              serviceAccountName: "argocd-installer",
              containers: [{
                name: "kubectl",
                image: "bitnami/kubectl:latest",
                command: ["/bin/sh", "-c"],
                args: [`curl -sSL ${manifestUrl} | kubectl apply -n ${namespace} -f -`]
              }],
              restartPolicy: "Never"
            }
          },
          backoffLimit: 0
        }
      }]
    });

    // Establecer dependencias correctas
    installerSA.node.addDependency(nsManifest);
    installerRoleBinding.node.addDependency(installerSA);
    applyArgoCD.node.addDependency(installerRoleBinding);

    return Promise.resolve(applyArgoCD);
  }
}
"}]}

