import { ClusterAddOn, ClusterInfo } from "@aws-quickstart/eks-blueprints"
import { Construct } from "constructs"
import * as eks from "aws-cdk-lib/aws-eks"
import * as cdk from "aws-cdk-lib"

export interface ArgoCDAddOnProps {
  namespace?: string
  version?: string
  values?: { [key: string]: any }
  createNamespace?: boolean
  cleanupEnabled?: boolean
}

export class ArgoCDAddOn implements ClusterAddOn {
  private readonly cleanupEnabled: boolean

  constructor(private props: ArgoCDAddOnProps = {}) {
    this.cleanupEnabled = props.cleanupEnabled ?? true
  }

  deploy(clusterInfo: ClusterInfo): Promise<Construct> {
    const cluster = clusterInfo.cluster
    const namespace = this.props.namespace ?? "argocd"
    const version = this.props.version ?? "5.51.4" // Versin actual del chart de Helm
    const createNamespace = this.props.createNamespace ?? true

    let chart: eks.HelmChart
    let mainResource: Construct

    if (createNamespace) {
      // Crear namespace para Argo CD
      const nsManifest = cluster.addManifest("argocd-namespace", {
        apiVersion: "v1",
        kind: "Namespace",
        metadata: {
          name: namespace,
        },
      })

      // Instalar Argo CD usando Helm
      chart = cluster.addHelmChart("argocd", {
        repository: "https://argoproj.github.io/argo-helm",
        chart: "argo-cd",
        release: "argocd",
        namespace: namespace,
        version: version,
        values: this.props.values,
      })

      chart.node.addDependency(nsManifest)
      mainResource = chart
    } else {
      // Instalar Argo CD usando Helm sin crear namespace
      chart = cluster.addHelmChart("argocd", {
        repository: "https://argoproj.github.io/argo-helm",
        chart: "argo-cd",
        release: "argocd",
        namespace: namespace,
        version: version,
        values: this.props.values,
      })
      mainResource = chart
    }

    // Agregar mecanismo de cleanup si est habilitado
    if (this.cleanupEnabled) {
      mainResource = this.setupCleanupMechanism(cluster, namespace, chart)
    }

    return Promise.resolve(mainResource)
  }

  private setupCleanupMechanism(
    cluster: eks.ICluster,
    namespace: string,
    chart: eks.HelmChart
  ): Construct {
    // Crear un recurso personalizado que se ejecutar durante la eliminacin
    const cleanupCR = new cdk.CustomResource(cluster, "argocd-cleanup-cr", {
      serviceToken: this.createCleanupProvider(cluster).serviceToken,
      properties: {
        // Incluir un timestamp para asegurar que el recurso se actualice cada vez
        timestamp: Date.now().toString(),
        namespace: namespace,
        releaseName: "argocd",
        clusterName: cluster.clusterName,
        region: cdk.Stack.of(cluster).region,
      },
      resourceType: "Custom::ArgoCDCleanup",
    })

    // Asegurar que el cleanup se ejecute despus de que se haya creado el chart
    cleanupCR.node.addDependency(chart)

    return cleanupCR
  }

  private createCleanupProvider(
    cluster: eks.ICluster
  ): cdk.custom_resources.Provider {
    // Crear una funcin Lambda que ejecutar el comando helm uninstall
    const fn = new cdk.aws_lambda.Function(cluster, "argocd-cleanup-lambda", {
      runtime: cdk.aws_lambda.Runtime.PYTHON_3_9,
      handler: "index.handler",
      code: cdk.aws_lambda.Code.fromInline(`
import boto3
import cfnresponse
import subprocess
import os
import logging

logger = logging.getLogger()
logger.setLevel(logging.INFO)

def handler(event, context):
    logger.info(f"Received event: {event}")
    response_data = {}

    try:
        request_type = event['RequestType']
        properties = event['ResourceProperties']

        # Solo ejecutar la limpieza cuando se elimina el recurso
        if request_type == 'Delete':
            cluster_name = properties['clusterName']
            region = properties['region']
            namespace = properties['namespace']
            release_name = properties['releaseName']

            logger.info(f"Iniciando desinstalacin de ArgoCD: {release_name} en namespace {namespace}")

            # Configurar kubectl para acceder al clster
            subprocess.check_call(['aws', 'eks', 'update-kubeconfig', 
                                  '--name', cluster_name, 
                                  '--region', region])

            # Desinstalar Argo CD usando Helm
            try:
                logger.info(f"Ejecutando helm uninstall {release_name} -n {namespace}")
                subprocess.check_call(['helm', 'uninstall', release_name, '-n', namespace])
                logger.info("Helm uninstall completado con xito")
            except subprocess.CalledProcessError as e:
                logger.warning(f"Error al desinstalar Helm chart: {str(e)}")
                # Continuar con la eliminacin del namespace incluso si falla la desinstalacin

            # Eliminar el namespace si existe
            try:
                logger.info(f"Eliminando namespace {namespace}")
                subprocess.check_call(['kubectl', 'delete', 'namespace', namespace, '--ignore-not-found'])
                logger.info("Namespace eliminado con xito")
            except subprocess.CalledProcessError as e:
                logger.warning(f"Error al eliminar namespace: {str(e)}")

            logger.info("Limpieza de ArgoCD completada")
            response_data['Message'] = "ArgoCD desinstalado correctamente"
        else:
            # Para Create y Update, simplemente devolver xito
            logger.info(f"Operacin {request_type} - no se requiere accin")
            response_data['Message'] = f"Operacin {request_type} procesada"

        cfnresponse.send(event, context, cfnresponse.SUCCESS, response_data)
    except Exception as e:
        logger.error(f"Error: {str(e)}")
        cfnresponse.send(event, context, cfnresponse.FAILED, {"Error": str(e)})
`),
      timeout: cdk.Duration.minutes(15),
    })

    // Agregar permisos necesarios para la funcin Lambda
    fn.addToRolePolicy(
      new cdk.aws_iam.PolicyStatement({
        actions: ["eks:DescribeCluster", "eks:ListClusters"],
        resources: ["*"],
      })
    )

    // Crear un proveedor personalizado para el recurso
    return new cdk.custom_resources.Provider(
      cluster,
      "argocd-cleanup-provider",
      {
        onEventHandler: fn,
      }
    )
  }
}

