    fn.addToRolePolicy(
      new cdk.aws_iam.PolicyStatement({
        actions: ["eks:DescribeCluster", "eks:ListClusters"],
        resources: ["*"],
      })
    )

    // Crear un proveedor personalizado para el recurso
    return new cdk.custom_resources.Provider(
      cluster,
      "argocd-core-cleanup-provider",
      {
        onEventHandler: fn,
      }
    )
  }
}
"}]}

