import { Construct } from "constructs"
import * as eks from "aws-cdk-lib/aws-eks"

export class ArgoCDCoreAddOn implements ClusterAddOn {
  constructor(private options?: {
    namespace?: string;
    version?: string;
  }) {}

  deploy(clusterInfo: ClusterInfo): Promise<Construct> {
    const cluster = clusterInfo.cluster
    const namespace = this.options?.namespace ?? "argocd"
    const version = this.options?.version ?? "v3.0.6"

    // Create namespace for Argo CD
    const nsManifest = cluster.addManifest("argocd-namespace", {
      apiVersion: "v1",
      kind: "Namespace",
      metadata: {
        name: namespace
      }
    });

    // Apply the Argo CD installation manifest from the official GitHub repository
    const argocdManifest = cluster.addHelmChart("argocd-core", {
      repository: "https://argoproj.github.io/argo-helm",
      chart: "argo-cd",
      release: "argocd",
      namespace: namespace,
      version: version.replace(/^v/, ''),
      values: {
        global: {
          image: {
            tag: version
          }
        }
      }
    });

    // Make sure the namespace is created before installing Argo CD
    argocdManifest.node.addDependency(nsManifest);

    return Promise.resolve(argocdManifest);
  }
}"}]}

